package Recursion;

import java.util.*;

public class Main {

    public static void main(String[] args) {
        long st = (System.nanoTime());
            String s =  "";
            int[] hashArray = new int[255];
            Arrays.fill(hashArray,-1);
            int maxLen = 0;
            int l = 0,r = 0;

        while(r<s.length()){
            if (hashArray[(int)s.charAt(r)] != -1){
                if(hashArray[(int)s.charAt(r)] >= l){
                    l = hashArray[(int)s.charAt(r)]+1;
                }
            }

            int len = r-l+1;
            maxLen = Math.max(len,maxLen);
            hashArray[(int)s.charAt(r)]  =  r;
            r++;
        }


        System.out.println(maxLen);
        System.out.println((System.nanoTime() - st));

        }


}

